import { MinecraftIdentifier } from "../identifier/MinecraftIdentifier";

export const NoTextureTexture =
  "data:image/png;base64,";

export class Item<
  IdentifierType extends MinecraftIdentifier = MinecraftIdentifier,
> {
  public readonly id: IdentifierType;
  public readonly displayName: string;
  public readonly count: number;
  public readonly texture: string = NoTextureTexture;

  constructor(
    id: IdentifierType,
    displayName: string,
    count: number,
    texture?: string,
  ) {
    this.id = id;
    this.displayName = displayName;
    this.count = count;
    this.texture = texture ?? NoTextureTexture;
  }
}
